import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getUsers, deleteUser } from "../../actions/actions";
import { useNavigate } from "react-router-dom";
import "./styles.scss";

const Dashboard = () => {
  const dispatch = useDispatch();
  const users = useSelector((state) => state.users);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch users from local database or API
    dispatch(getUsers());
  }, [dispatch]);

  const handleDeleteUser = (userId) => {
    if (window.confirm("Are you sure you want to delete this user?")) {
      dispatch(deleteUser(userId));
    }
  };

  const handleViewDetails = (userId) => {
    // Navigate to user details page
    navigate(`/user-details/${userId}`);
  };

  const handleEditUser = (userId) => {
    // Navigate to user edit page
    navigate(`/edit-user/${userId}`);
  };

  const handleAddUser = () => {
    // Navigate to add user page
    navigate("/add-user");
  };

  // Implement other functionalities such as sorting, searching, etc.

  return (
    <div className="dashboard">
      <div className="dashboard">

      </div>
      {users.length > 0 ? (
        <ul className="user-list">
          {users.map((user) => (
            <li key={user._id} style={{ listStyle: "none" }}>
              <div>
                <strong>{user.name}</strong>
                <p>{user.email}</p>
                <p>{user.phone}</p>
              </div>
              <div className="actions">
                <button onClick={() => handleDeleteUser(user._id)}>
                  Delete
                </button>
                <button onClick={() => handleViewDetails(user._id)}>
                  View Details
                </button>
                <button onClick={() => handleEditUser(user._id)}>Edit</button>
                {/* Add other actions */}
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <div className="no-data-found">No Data Found</div>
      )}

      <button className="floating-button" onClick={handleAddUser}>
        Add User
      </button>
    </div>
  );
};

export default Dashboard;
