import React, { useState, useRef } from "react";
// import Camera from "react-camera-hook";
// import Gallery from "react-image-gallery";
import { useSelector, useDispatch } from "react-redux";
import { saveUser } from "../";
import "./styles.scss";

const SignupForm = () => {
  const dispatch = useDispatch();
  const user = useSelector((state) => state.user);

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [gender, setGender] = useState("");
  const [heardAbout, setHeardAbout] = useState([]);
  const [city, setCity] = useState("");
  const [state, setState] = useState("");
  const [password, setPassword] = useState("");
  //   const [images, setImages] = useState([]);
  //   const cameraRef = useRef(null);

  const handleSave = () => {
    // Save user data to Redux store
    dispatch(saveUser({ name, email, phone, gender, heardAbout, city, state }));
  };

  //   const handleTakePicture = async () => {
  //     if (cameraRef.current) {
  //       const picture = await cameraRef.current.takePhoto();
  //       setImages([...images, { original: picture }]);
  //     }
  //   };

  //   const handleGalleryImage = (event) => {
  //     const file = event.target.files[0];
  //     const reader = new FileReader();
  //     reader.onloadend = () => {
  //       setImages([...images, { original: reader.result }]);
  //     };
  //     if (file) {
  //       reader.readAsDataURL(file);
  //     }
  //   };

  return (
    <div className="signup-form">
      <label>
        Name:
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value.replace(/[^A-Za-z ]/g, ""))}
        />
      </label>

      <label>
        Email:
        <input
          type="text"
          value={email}
          onChange={(e) =>
            setEmail(e.target.value.replace(/[^A-Za-z0-9@.]/g, ""))
          }
        />
      </label>

      <label>
        Phone:
        <input
          type="text"
          value={phone}
          onChange={(e) => setPhone(e.target.value.replace(/[^0-9]/g, ""))}
        />
      </label>

      <label>
        Gender:
        <div>
          <label>
            <input
              type="radio"
              value="Male"
              checked={gender === "Male"}
              onChange={() => setGender("Male")}
            />
            Male
          </label>

          <label>
            <input
              type="radio"
              value="Female"
              checked={gender === "Female"}
              onChange={() => setGender("Female")}
            />
            Female
          </label>

          <label>
            <input
              type="radio"
              value="Others"
              checked={gender === "Others"}
              onChange={() => setGender("Others")}
            />
            Others
          </label>
        </div>
      </label>

      <label>
        How did you hear about this?
        <div>
          <label>
            <input
              type="checkbox"
              value="LinkedIn"
              checked={heardAbout.includes("LinkedIn")}
              onChange={() =>
                setHeardAbout((prev) =>
                  prev.includes("LinkedIn")
                    ? prev.filter((item) => item !== "LinkedIn")
                    : [...prev, "LinkedIn"]
                )
              }
            />
            LinkedIn
          </label>

          <label>
            <input
              type="checkbox"
              value="Friends"
              checked={heardAbout.includes("Friends")}
              onChange={() =>
                setHeardAbout((prev) =>
                  prev.includes("Friends")
                    ? prev.filter((item) => item !== "Friends")
                    : [...prev, "Friends"]
                )
              }
            />
            Friends
          </label>

          <label>
            <input
              type="checkbox"
              value="Job Portal"
              checked={heardAbout.includes("Job Portal")}
              onChange={() =>
                setHeardAbout((prev) =>
                  prev.includes("Job Portal")
                    ? prev.filter((item) => item !== "Job Portal")
                    : [...prev, "Job Portal"]
                )
              }
            />
            Job Portal
          </label>

          <label>
            <input
              type="checkbox"
              value="Others"
              checked={heardAbout.includes("Others")}
              onChange={() =>
                setHeardAbout((prev) =>
                  prev.includes("Others")
                    ? prev.filter((item) => item !== "Others")
                    : [...prev, "Others"]
                )
              }
            />
            Others
          </label>
        </div>
      </label>

      <label>
        City:
        <select value={city} onChange={(e) => setCity(e.target.value)}>
          <option value="">Select City</option>
          <option value="Mumbai">Mumbai</option>
          <option value="Pune">Pune</option>
          <option value="Ahmedabad">Ahmedabad</option>
        </select>
      </label>

      <label>
        State:
        <input
          type="text"
          value={state}
          onChange={(e) => setState(e.target.value)}
          placeholder="Type to search"
        />
        {/* You might want to add auto-suggestion logic here */}
      </label>
      <label>
        Password:
        <input
          type="text"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Set the password"
        />
        {/* You might want to add auto-suggestion logic here */}
      </label>
      {/* <div className="camera-container">
        <label>
          Take Picture:
          <div>
            <Camera ref={cameraRef} />
            <button onClick={handleTakePicture}>Capture</button>
          </div>
        </label>
      </div>

      <div className="gallery-container">
        <label>
          Upload from Gallery:
          <input type="file" accept="image/*" onChange={handleGalleryImage} />
        </label>

        <label>
          Gallery:
          <Gallery items={images} />
        </label>
      </div> */}
      <button onClick={handleSave}>Save</button>
    </div>
  );
};

export default SignupForm;
