          <input
            type="text"
            value={user[0].mobile}
            onChange={(e) => setPhone(e.target.value.replace(/[^0-9]/g, ""))}
            placeholder="mobile"
          />
        </label>
      </div>{" "}
      <div className="buttons">
        <button onClick={handleSaveUser}>Save</button>
        <Link style={{ textDecoration: "none" }} to={"/dashboard"}>
          <button>Cancel</button>
        </Link>
      </div>
    </div>
  );
};
