import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getUsers, deleteUser } from "../../actions/actions";
import { Link, useNavigate } from "react-router-dom";
import "./styles.scss";
import EditUserData from "./EditUserData/EditUserData";

const Dashboard = () => {
  const dispatch = useDispatch();
  const users = useSelector((state) => state.users);
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [searchKeyword, setSearchKeyword] = useState("");
  const [filterOption, setFilterOption] = useState("A-Z");
  const navigate = useNavigate();
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10; // Adjust this based on the number of items you want to display per page

  useEffect(() => {
    // Fetch users from local database or API
    dispatch(getUsers());
  }, [dispatch]);

  const handleDeleteUser = () => {
    if (window.confirm("Are you sure you want to delete selected users?")) {
      // Implement delete functionality here
      // dispatch(deleteUser(userId));
    }
  };

  const handleViewDetails = () => {
    navigate("/user-details");
  };
  const handleEditUser = (userId) => {
    navigate(`/edit-user/${userId}`);
  };

  const handleAddUser = () => {};

  const onPrevData = () => {
    if (currentPage > 1) {
      setCurrentPage((prevPage) => prevPage - 1);
    }
  };

  const onNextData = () => {
    const totalPages = Math.ceil(users.length / itemsPerPage);
    if (currentPage < totalPages) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  return (
    <div className="dashboard">
      <div className="dashboard-table">
        <div className="dashboard-header"></div>
        <div className="table-header">
          <div className="add-container">
            <h4>All ({users.length})</h4>
            <button className="add-user-btn">Add User</button>
          </div>
          <div className="search-and-filter">
            <input
              type="text"
              placeholder="Search by Name, Mobile, or Email"
              value={searchKeyword}
              onChange={(e) => setSearchKeyword(e.target.value)}
            />
            <select
              value={filterOption}
              onChange={(e) => setFilterOption(e.target.value)}
            >
              <option value="A-Z">A-Z</option>
              <option value="Z-A">Z-A</option>
              <option value="LastModified">Last Modified</option>
              <option value="LastInserted">Last Inserted</option>
            </select>
          </div>
        </div>
        <table>
          <thead>
            <tr>
              <th>
                <input
                  type="checkbox"
                  onChange={() =>
                    setSelectedUsers(users.map((user) => user.id))
                  }
                />
              </th>
              <th>Username</th>
              <th>Email</th>
              <th>Phone</th>
              <th>Edit</th>
              <th>Delete User</th>
            </tr>
          </thead>
          <tbody>
            {/* Map through users based on search and filter criteria */}
            {users.map((user) => (
              <tr key={user._id}>
                <td>
                  <input
                    type="checkbox"
                    checked={selectedUsers.includes(user._id)}
                    onChange={() =>
                      setSelectedUsers((prev) =>
                        prev.includes(user.id)
                          ? prev.filter((id) => id !== user._id)
                          : [...prev, user.id]
                      )
                    }
                  />
                </td>
                <td>
                  <img
                    src={`${
                      user.image
                        ? user.image
                        : "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSUrgu4a7W_OM8LmAuN7Prk8dzWXm7PVB_FmA&usqp=CAU"
                    }`}
                    alt="User"
                  />
                </td>

                <td>
                  <Link
                    style={{ textDecoration: "none" }}
                    to={`/user-details/${user._id}`}
                  >
                    {user.name}
                  </Link>
                </td>
                <td>
                  <Link
                    style={{ textDecoration: "none" }}
                    to={`/user-details/${user._id}`}
                  >
                    {user.email}{" "}
                  </Link>
                </td>
                <td>
                  <Link
                    style={{ textDecoration: "none" }}
                    to={`/user-details/${user._id}`}
                  >
                    {user.phone}{" "}
                  </Link>
                </td>
                <td>
                  <button
                    className="delete-btn"
                    onClick={() => handleEditUser(user._id)}
                  >
                    Edit
                  </button>
                </td>
                <td>
                  <button
                    className="delete-btn"
                    onClick={() => handleDeleteUser(user.id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="table-footer">
          <div className="pagination">
            Showing 1 to 10 of {users.length} entries
          </div>
          <div className="pagination-buttons">
            <button onClick={onPrevData}>Prev</button>
            <p>1</p>
            <p>2</p>
            <p>3</p>
            <button onClick={onNextData}>Next</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
