const API_URL = "http://localhost:3001/api";

export const fetchUsersFromApi = async () => {
  try {
    const response = await fetch(`${API_URL}/users`);
    const data = await response.json();
    return data;
  } catch (error) {
    console.error("Error fetching users from API:", error);
    throw error;
  }
};



