import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateUser } from "../../../actions/actions";
import "./styles.scss";
import { Link, useParams, useNavigate } from "react-router-dom";
import Header from "../../header/header";

const EditUserDetails = () => {
  const { userId } = useParams();
  const users = useSelector((state) => state.users);
  const user = users.find((eachUser) => eachUser._id === userId); // Use find instead of filter
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");

  // Check if user[0] exists before accessing its properties
  const userName = user ? user.name : "";
  const userEmail = user ? user.email : "";
  const userMobile = user ? user.mobile : "";

  const handleUpdateUser = () => {
    const createdAt = new Date();
    const updatedUser = { name, email, mobile, createdAt };
    dispatch(updateUser(updatedUser));
    navigate("/dashboard");
  };

  return (
    <>
      <Header />{" "}
      <div className="Edit-user">
        <h2>Edit Details</h2>
        <div className="inputs-container">
          <label>
            Name:
            <input
              type="text"
              value={userName}
              onChange={(e) =>
                setName(e.target.value.replace(/[^A-Za-z ]/g, ""))
              }
              placeholder="Name"
            />
          </label>
          <label>
            Email:
            <input
              type="text"
              value={userEmail}
              onChange={(e) =>
                setEmail(e.target.value.replace(/[^A-Za-z0-9@.]/g, ""))
              }
              placeholder="Email"
            />
          </label>
          <label>
            Mobile:
            <input
              type="text"
              value={userMobile}
              onChange={(e) => setMobile(e.target.value.replace(/[^0-9]/g, ""))}
              placeholder="Mobile"
            />
          </label>
        </div>
        <div className="buttons">
          <button onClick={handleUpdateUser}>Update</button>
          <Link style={{ textDecoration: "none" }} to={"/dashboard"}>
            <button>Cancel</button>
          </Link>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default EditUserDetails;
