import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getUsers, deleteUser } from "../../actions/actions";
import { useNavigate } from "react-router-dom";
import "./styles.scss";

const Dashboard = () => {
  const dispatch = useDispatch();
  const users = useSelector((state) => state.users);
  const navigate = useNavigate();
 const [selectedUsers, setSelectedUsers] = useState([]);
 const [searchKeyword, setSearchKeyword] = useState("");
 const [filterOption, setFilterOption] = useState("A-Z");

 useEffect(() => {
   // Fetch users from local database or API
   dispatch(getUsers());
 }, [dispatch]);

 const handleDeleteUser = () => {
   if (window.confirm("Are you sure you want to delete selected users?")) {
     // Implement delete functionality here
     // dispatch(deleteUser(userId));
   }
  };
  
  
  

  return (
    <div className="dashboard">
      <div className="dashboard-table">
        <div className="dashboard-header"></div>
        <div className="table-header">
          <button className="add-user-btn">Add User</button>
          <div className="search-and-filter">
            <input
              type="text"
              placeholder="Search by Name, Mobile, or Email"
              value={searchKeyword}
              onChange={(e) => setSearchKeyword(e.target.value)}
            />
            <select
              value={filterOption}
              onChange={(e) => setFilterOption(e.target.value)}
            >
              <option value="A-Z">A-Z</option>
              <option value="Z-A">Z-A</option>
              <option value="LastModified">Last Modified</option>
              <option value="LastInserted">Last Inserted</option>
            </select>
          </div>
        </div>
        <table>
          <thead>
            <tr>
              <th>
                <input
                  type="checkbox"
                  onChange={() =>
                    setSelectedUsers(users.map((user) => user.id))
                  }
                />
              </th>
              <th>Image</th>
              <th>Username</th>
              <th>Email</th>
              <th>Phone</th>
              <th>Delete User</th>
            </tr>
          </thead>
          <tbody>
            {/* Map through users based on search and filter criteria */}
            {users.map((user) => (
              <tr key={user.id}>
                <td>
                  <input
                    type="checkbox"
                    checked={selectedUsers.includes(user.id)}
                    onChange={() =>
                      setSelectedUsers((prev) =>
                        prev.includes(user.id)
                          ? prev.filter((id) => id !== user.id)
                          : [...prev, user.id]
                      )
                    }
                  />
                </td>
                <td>
                  {/* Display user image here */}
                  <img src={user.image} alt="User" />
                </td>
                <td>{user.username}</td>
                <td>{user.email}</td>
                <td>{user.phone}</td>
                <td>
                  <button
                    className="delete-btn"
                    onClick={() => handleDeleteUser(user.id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="table-footer">
          <div className="pagination">
            Showing 1 to 10 of {users.length} entries
          </div>
          <div className="pagination-buttons">
            {/* Implement pagination buttons here */}
            <button>Prev</button>
            <button>Next</button>
          </div>
        </div>
      </div>
      {users.length > 0 ? (
        <ul className="user-list">
          {users.map((user) => (
            <li key={user._id} style={{ listStyle: "none" }}>
              <div>
                <strong>{user.name}</strong>
                <p>{user.email}</p>
                <p>{user.phone}</p>
              </div>
              <div className="actions">
                <button onClick={() => handleDeleteUser(user._id)}>
                  Delete
                </button>
                <button onClick={() => handleViewDetails(user._id)}>
                  View Details
                </button>
                <button onClick={() => handleEditUser(user._id)}>Edit</button>
                {/* Add other actions */}
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <div className="no-data-found">No Data Found</div>
      )}

      <button className="floating-button" onClick={handleAddUser}>
        Add User
      </button>
    </div>
  );
};

export default Dashboard;
