import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateUser } from "../../../actions/actions";
import "./styles.scss";
import { Link, useParams, useNavigate } from "react-router-dom";
import Header from "../../header/header";
import Footer from "../../footer/footer";

const EditUserDetails = () => {
  const { userId } = useParams();
  const users = useSelector((state) => state.users);
  const user = users.find((eachUser) => eachUser._id === userId); // Use find instead of filter
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");

  // Check if user[0] exists before accessing its properties
  const userName = user ? user.name : "";
  const userEmail = user ? user.email : "";
  const userMobile = user ? user.mobile : "";

  const handleUpdateUser = () => {
    const createdAt = new Date();
    const updatedUser = { name, email, mobile, createdAt };
    dispatch(updateUser(updatedUser));
    navigate("/dashboard");
  };

  return (
   <><Header/><Footer/></>
  );
};

export default EditUserDetails;
