// Dashboard.js

import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getUsers, deleteUser } from "../";
import "./styles.scss";

const Dashboard = () => {
  const dispatch = useDispatch();
  const users = useSelector((state) => state.users);

  useEffect(() => {
    // Fetch users from local database or API
    dispatch(getUsers());
  }, [dispatch]);

  const handleDeleteUser = (userId) => {
    if (window.confirm("Are you sure you want to delete this user?")) {
      dispatch(deleteUser(userId));
    }
  };

  // Implement other functionalities such as sorting, searching, etc.

  return (
    <div className="dashboard">
      {users.length > 0 ? (
        <ul className="user-list">
          {users.map((user) => (
            <li key={user.id}>
              <div>
                <strong>{user.username}</strong>
                <p>{user.email}</p>
                <p>{user.phone}</p>
              </div>
              <div className="actions">
                <button onClick={() => handleDeleteUser(user.id)}>
                  Delete
                </button>
                {/* Add other actions (edit, view details, etc.) */}
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <div className="no-data-found">No Data Found</div>
      )}

      {/* Add the floating button or options menu to add new user */}
    </div>
  );
};

export default Dashboard;
